document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll("[data-slug-preview]").forEach(element=>{const default_value=element.dataset.slugDefault??"unknown",input=element.closest(".setting").querySelector('input[name="'+element.dataset.slugPreview+'"]');var update=()=>{element.innerHTML=(input.value||default_value).replace("{entry_id}","123")};input.addEventListener("input",update),update()})});